mapper.insert:
INSERT INTO tb_smell_register (
                                SMELL_REGISTER_NO
                                , SMELL_TYPE
                                , SMELL_VALUE
                                , WEATHER_STATE
                                , TEMPERATURE_VALUE
                                , HUMIDITY_VALUE
                                , WIND_DIRECTION_VALUE
                                , WIND_SPEED_VALUE
                                , GPS_X
                                , GPS_Y
                                , SMELL_COMMENT
                                , SMELL_REGISTER_TIME
                                , REG_ID
                                , AIR_SENSOR_NAME
                                , AIR_SENSING_DATE
                                , PM10_AVG
                              )
                      VALUES (
                                #{smellRegisterNo}
                                , #{smellType}
                                , #{smellValue}
                                , #{weatherState}
                                , #{temperatureValue}
                                , #{humidityValue}
                                , #{windDirectionValue}
                                , #{windSpeedValue}
                                , #{gpsX}
                                , #{gpsY}
                                , #{smellComment}
                                , #{smellRegisterTime}
                                , #{regId}
                                , #{airSensorName}
                                , #{airSensingDate}
                                , #{pm10Avg}
                             )

INSERT INTO tb_smell_image (
                                SMELL_IMAGE_NO
                                , SMELL_IMAGE_PATH
                                , SMELL_ORIGINAL_PATH
                                , SMELL_REGISTER_NO
                            )
                    VALUES (
                                (SELECT CONCAT('IM',DATE_FORMAT(now(), '%Y%m%d%H%i%s'),(SELECT IFNULL(LPAD(MAX(substr(t2.SMELL_IMAGE_NO,17,2))+1,'2','0'),'01') FROM tb_smell_image t2 WHERE substr(t2.SMELL_IMAGE_NO,1,16) = CONCAT('IM',DATE_FORMAT(now(), '%Y%m%d%H%i%s')))) SMELL_IMAGE_NO FROM DUAL)
                                , #{smellImagePath}
                                , #{smellOriginalPath}
                                , #{smellRegisterNo}
                            )
