                        USER_ID
                        , USER_PASSWORD
                        , USER_TYPE
                        , USER_NAME
                        , USER_SEX
                        , USER_PHONE
                     )
             VALUES (
                        #{userId}
                        , password(#{userPassword})
                        , #{userType}                                                                            
                        , #{userName}
                        , #{userSex}
                        , replace(#{userPhone},'-','')
                     )
