mapper.select:
SELECT ANY_VALUE(tsr.SMELL_REGISTER_NO) SMELL_REGISTER_NO , ANY_VALUE(tsr.SMELL_TYPE) SMELL_TYPE , ANY_VALUE(F_GET_CODE_NAME('STY',SMELL_TYPE)) SMELL_TYPE_NAME , ANY_VALUE(tsr.SMELL_VALUE) SMELL_VALUE , ANY_VALUE(F_GET_CODE_NAME('SMT',SMELL_VALUE)) SMELL_VALUE_NAME , ANY_VALUE(tsr.WEATHER_STATE) WEATHER_STATE , ANY_VALUE(F_GET_CODE_NAME('WET',WEATHER_STATE)) WEATHER_STATE_NAME , ANY_VALUE(tsr.TEMPERATURE_VALUE) TEMPERATURE_VALUE , ANY_VALUE(tsr.HUMIDITY_VALUE) HUMIDITY_VALUE , ANY_VALUE(tsr.WIND_DIRECTION_VALUE) WIND_DIRECTION_VALUE , ANY_VALUE(F_GET_CODE_NAME('WND',WEATHER_STATE)) WIND_DIRECTION_VALUE_NAME , ANY_VALUE(tsr.WIND_SPEED_VALUE) WIND_SPEED_VALUE , ANY_VALUE(tsr.GPS_X) GPS_X , ANY_VALUE(tsr.GPS_Y) GPS_Y , ANY_VALUE(tsr.SMELL_COMMENT) SMELL_COMMENT , ANY_VALUE(tsr.SMELL_REGISTER_TIME) SMELL_REGISTER_TIME , ANY_VALUE(F_GET_CODE_NAME('REN',SMELL_REGISTER_TIME)) SMELL_REGISTER_TIME_NAME , tsr.REG_ID REG_ID , tu.USER_NAME USER_NAME , DATE_FORMAT(tsr.REG_DT,'%Y-%m-%d %H:%i:%S') REG_DT FROM tb_smell_register tsr left join tb_user tu on tsr.REG_ID = tu.USER_ID WHERE 1 = 1 AND SIGN_IN_STATUS = 'Y' AND tu.REG_GBN = '004'
AND tu.USER_REGION_DETAIL = #{userRegionDetail}
AND tsr.SMELL_TYPE = #{smellType}
AND tsr.SMELL_VALUE = #{smellValue}
AND tsr.REG_DT >= #{startDate} AND tsr.REG_DT < #{endDate}

select tu.USER_REGION_DETAIL as CODE_ID ,(select CODE_ID_NAME from tb_code tc where tc.CODE_ID = tu.USER_REGION_DETAIL and tc.CODE_GROUP = 'RGD') as USER_REGION_DETAIL ,count(*) as TOTAL_COUNT ,case when tu.USER_REGION_DETAIL = '001' then '126.3162828' when tu.USER_REGION_DETAIL = '002' then '126.271862' when tu.USER_REGION_DETAIL = '003' then '126.2981186' when tu.USER_REGION_DETAIL = '004' then '126.2756824' when tu.USER_REGION_DETAIL = '005' then '126.2392054' when tu.USER_REGION_DETAIL = '006' then '126.2421586' when tu.USER_REGION_DETAIL = '007' then '126.4289539' when tu.USER_REGION_DETAIL = '008' then '126.4508866' when tu.USER_REGION_DETAIL = '009' then '126.2700715' when tu.USER_REGION_DETAIL = '010' then '126.2194108' when tu.USER_REGION_DETAIL = '011' then '126.2313473' when tu.USER_REGION_DETAIL = '012' then '126.2475706' when tu.USER_REGION_DETAIL = '013' then '126.7313785' when tu.USER_REGION_DETAIL = '014' then '126.1799307' end as GPS_X ,case when tu.USER_REGION_DETAIL = '001' then '33.3515554' when tu.USER_REGION_DETAIL = '002' then '33.3600196' when tu.USER_REGION_DETAIL = '003' then '33.3961452' when tu.USER_REGION_DETAIL = '004' then '33.379419' when tu.USER_REGION_DETAIL = '005' then '33.3700283' when tu.USER_REGION_DETAIL = '006' then '33.3511661' when tu.USER_REGION_DETAIL = '007' then '33.4191693' when tu.USER_REGION_DETAIL = '008' then '33.4034138' when tu.USER_REGION_DETAIL = '009' then '33.3279794' when tu.USER_REGION_DETAIL = '010' then '33.3212026' when tu.USER_REGION_DETAIL = '011' then '33.2456108' when tu.USER_REGION_DETAIL = '012' then '33.247276' when tu.USER_REGION_DETAIL = '013' then '33.3828211' when tu.USER_REGION_DETAIL = '014' then '33.3238904' end as GPS_Y from tb_smell_register tsr left join tb_user tu on tsr.REG_ID = tu.USER_ID where REG_GBN = '004'
AND tu.USER_REGION_DETAIL = #{userRegionDetail}
AND tsr.SMELL_TYPE = #{smellType}
AND tsr.SMELL_VALUE = #{smellValue}
AND tsr.REG_DT >= #{startDate} AND tsr.REG_DT < #{endDate}
group by tu.USER_REGION_DETAIL

SELECT CODE_GROUP
     , CODE_GROUP_NAME
     , CODE_ID
     , CODE_ID_NAME
     , CODE_COMMENT

  FROM tb_code

 WHERE 1=1

   AND CODE_GROUP = #{codeGroup}


    AND REFERENCE_CODE_GROUP = #{referenceCodeGroup}



    AND REFERENCE_CODE_ID = #{referenceCodeId}
