mapper.select:
SELECT USER_ID , USER_NAME , USER_REGION_MASTER , USER_REGION_DETAIL , CONCAT(F_GET_CODE_NAME('REM',USER_REGION_MASTER), ' ',F_GET_CODE_NAME('RGD',USER_REGION_DETAIL) ) USER_REGION_NAME FROM tb_user WHERE 1=1
AND LOWER(USER_NAME) LIKE LOWER('%${userName}%')
AND USER_REGION_MASTER = #{userRegionMaster}
AND USER_REGION_DETAIL = #{userRegionDetail}
ORDER BY USER_ID DESC
LIMIT #{recordCountPerPage} OFFSET #{firstIndex};

SELECT COUNT(*) cnt
FROM tb_user
WHERE 1=1

    AND LOWER(USER_NAME) LIKE LOWER('%${userName}%')


    AND USER_REGION_MASTER = #{userRegionMaster}


    AND USER_REGION_DETAIL = #{userRegionDetail}
