mapper.select:
SELECT USER_ID , USER_TYPE , F_GET_CODE_NAME('UST',USER_TYPE) USER_TYPE_NAME , USER_NAME , USER_REGION , F_GET_CODE_NAME('RGN',USER_REGION) USER_REGION_NAME , USER_SEX , F_GET_CODE_NAME('SEX',USER_SEX) USER_SEX_NAME , USER_AGE , REG_DT FROM tb_user WHERE 1=1
AND (LOWER(USER_ID) LIKE LOWER('%${userId}%') OR LOWER(USER_NAME) LIKE LOWER('%${userId}%'))
AND USER_REGION = #{userRegion}
AND USER_AGE = #{userAge}
AND USER_TYPE = #{userType}
AND USER_SEX = #{userSex}
ORDER BY REG_DT DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex};

SELECT COUNT(*) cnt
FROM tb_user
WHERE 1=1


    AND (LOWER(USER_ID) LIKE LOWER('%${userId}%') OR LOWER(USER_NAME) LIKE LOWER('%${userId}%'))


    AND USER_AGE = #{userAge}


    AND USER_REGION = #{userRegion}


    AND USER_TYPE = #{userType}


    AND USER_SEX = #{userSex}

mapper.update:
UPDATE tb_user
   SET USER_PASSWORD = password(#{userPassword})
 WHERE USER_ID = #{userId};
