mapper.select:
SELECT USER_ID , USER_TYPE , F_GET_CODE_NAME('UST',USER_TYPE) USER_TYPE_NAME , USER_NAME , USER_REGION_MASTER , USER_REGION_DETAIL , CASE LENGTH(USER_PHONE) WHEN 11 THEN CONCAT(LEFT(USER_PHONE, 3), '-', MID(USER_PHONE, 4, 4), '-', RIGHT(USER_PHONE, 4)) END USER_PHONE , F_GET_CODE_NAME('REM',USER_REGION_MASTER) USER_REGION_MASTER_NAME , F_GET_CODE_NAME('RGD',USER_REGION_DETAIL) USER_REGION_DETAIL_NAME , CONCAT(F_GET_CODE_NAME('REM',USER_REGION_MASTER), ' ',F_GET_CODE_NAME('RGD',USER_REGION_DETAIL) ) USER_REGION_NAME , USER_SEX , F_GET_CODE_NAME('SEX',USER_SEX) USER_SEX_NAME , USER_AGE , REG_DT FROM tb_user WHERE 1=1
AND (LOWER(USER_ID) LIKE LOWER('%${userId}%') OR LOWER(USER_NAME) LIKE LOWER('%${userId}%'))
AND USER_REGION_MASTER = #{userRegionMaster}
AND USER_AGE LIKE '%${userAge}%'
AND USER_SEX = #{userSex}
AND USER_TYPE = #{userType}
ORDER BY REG_DT DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex};

SELECT COUNT(*) cnt
FROM tb_user
WHERE 1=1


    AND (LOWER(USER_ID) LIKE LOWER('%${userId}%') OR LOWER(USER_NAME) LIKE LOWER('%${userId}%'))


    AND USER_REGION_MASTER = #{userRegionMaster}


    AND USER_AGE LIKE '%${userAge}%'


    AND USER_SEX = #{userSex}


    AND USER_TYPE = #{userType}

mapper.update:
UPDATE tb_user
   SET USER_PASSWORD = password(#{userPassword})
 WHERE USER_ID = #{userId};
