SELECT SMELL_VALUE , F_GET_CODE_NAME('SMT',A.SMELL_VALUE) SMELL_VALUE_NAME
, COUNT(SMELL_VALUE) CNT , DATE_FORMAT(REG_DT, '%Y-%m-%d') REG_DT FROM tb_smell_register A LEFT JOIN ( SELECT USER_ID , USER_REGION_MASTER , USER_REGION_DETAIL FROM ( SELECT a.USER_ID USER_ID, a.USER_REGION_MASTER USER_REGION_MASTER, a.USER_REGION_DETAIL USER_REGION_DETAIL FROM tb_user a INNER JOIN ( SELECT USER_ID, MAX(REG_GBN) REG_GBN FROM tb_user GROUP BY USER_ID ) b ON a.USER_ID = b.USER_ID AND a.REG_GBN = b.REG_GBN ) tb_user ) B ON A.REG_ID = B.USER_ID WHERE 1=1
AND B.USER_REGION_MASTER = #{userRegionMaster}
AND B.USER_REGION_DETAIL = #{userRegionDetail}
AND DATE_FORMAT(REG_DT, '%Y-%m-%d') BETWEEN #{searchStart} AND #{searchEnd} GROUP BY SMELL_VALUE, DATE_FORMAT(REG_DT, '%Y-%m-%d') ORDER BY REG_DT, SMELL_VALUE
, COUNT(SMELL_VALUE) CNT , DATE_FORMAT(REG_DT, '%Y-%m') REG_DT
FROM tb_smell_register A LEFT JOIN ( SELECT USER_ID , USER_REGION_MASTER , USER_REGION_DETAIL FROM ( SELECT a.USER_ID USER_ID, a.USER_REGION_MASTER USER_REGION_MASTER, a.USER_REGION_DETAIL USER_REGION_DETAIL FROM tb_user a INNER JOIN ( SELECT USER_ID, MAX(REG_GBN) REG_GBN FROM tb_user GROUP BY USER_ID ) b ON a.USER_ID = b.USER_ID AND a.REG_GBN = b.REG_GBN ) tb_user ) B ON A.REG_ID = B.USER_ID WHERE 1=1
AND B.USER_REGION_MASTER = #{userRegionMaster}
AND B.USER_REGION_DETAIL = #{userRegionDetail}
AND DATE_FORMAT(REG_DT, '%Y-%m') BETWEEN #{searchStart} AND #{searchEnd} GROUP BY SMELL_VALUE, DATE_FORMAT(REG_DT, '%Y-%m') ORDER BY REG_DT, SMELL_VALUE
, COUNT(SMELL_VALUE) CNT , DATE_FORMAT(REG_DT, '%Y') REG_DT
FROM tb_smell_register A LEFT JOIN ( SELECT USER_ID , USER_REGION_MASTER , USER_REGION_DETAIL FROM ( SELECT a.USER_ID USER_ID, a.USER_REGION_MASTER USER_REGION_MASTER, a.USER_REGION_DETAIL USER_REGION_DETAIL FROM tb_user a INNER JOIN ( SELECT USER_ID, MAX(REG_GBN) REG_GBN FROM tb_user GROUP BY USER_ID ) b ON a.USER_ID = b.USER_ID AND a.REG_GBN = b.REG_GBN ) tb_user ) B ON A.REG_ID = B.USER_ID WHERE 1=1
AND B.USER_REGION_MASTER = #{userRegionMaster}
AND B.USER_REGION_DETAIL = #{userRegionDetail}
AND DATE_FORMAT(REG_DT, '%Y') BETWEEN #{searchStart} AND #{searchEnd}
GROUP BY SMELL_VALUE, DATE_FORMAT(REG_DT, '%Y') ORDER BY REG_DT, SMELL_VALUE
, COUNT(SMELL_VALUE) CNT , F_GET_CODE_NAME('REN',A.SMELL_REGISTER_TIME) REG_DT FROM tb_smell_register A LEFT JOIN ( SELECT USER_ID , USER_REGION_MASTER , USER_REGION_DETAIL FROM ( SELECT a.USER_ID USER_ID, a.USER_REGION_MASTER USER_REGION_MASTER, a.USER_REGION_DETAIL USER_REGION_DETAIL FROM tb_user a INNER JOIN ( SELECT USER_ID, MAX(REG_GBN) REG_GBN FROM tb_user GROUP BY USER_ID ) b ON a.USER_ID = b.USER_ID AND a.REG_GBN = b.REG_GBN ) tb_user ) B ON A.REG_ID = B.USER_ID WHERE 1=1
AND B.USER_REGION_MASTER = #{userRegionMaster}
AND B.USER_REGION_DETAIL = #{userRegionDetail}
AND DATE_FORMAT(A.REG_DT, '%Y-%m-%d') = DATE_FORMAT(curdate(), '%Y-%m-%d') AND A.SMELL_REGISTER_TIME IN( #{searchStart}
,'002'
,'003'
) GROUP BY SMELL_VALUE, A.SMELL_REGISTER_TIME ORDER BY A.SMELL_REGISTER_TIME, SMELL_VALUE

SELECT *, ROUND(IFNull(result.userRegisterCount/result.userTotalCount*100,0),2) userRegisterPercentage, IFNull(F_GET_CODE_NAME('SMT', smellResult.mainSmellValue),'') mainSmellValueName, IFNULL(F_GET_CODE_NAME('STY',smellResult.mainSmellType),'') mainSmellTypeName, #{smellRegisterTime} smellRegisterTime, ifNull(F_GET_CODE_NAME('REN',#{smellRegisterTime}),'') smellRegisterTimeName FROM ( SELECT COUNT(*) userTotalCount, COUNT(CASE WHEN SMELL_VALUE != '001' AND SMELL_TYPE != '008' THEN 1 END) userRegisterCount FROM ( SELECT t1.SMELL_VALUE SMELL_VALUE, t1.SMELL_TYPE SMELL_TYPE, t1.REG_DT REG_DT, t1.SMELL_REGISTER_TIME SMELL_REGISTER_TIME, t1.REG_ID REG_ID FROM tb_smell_register t1, ( SELECT MAX(SMELL_VALUE) SMELL_VALUE, REG_DT, SMELL_REGISTER_TIME, REG_ID FROM tb_smell_register WHERE 1 = 1 AND DATE_FORMAT(REG_DT, '%Y-%m-%d') BETWEEN #{searchStart} AND #{searchEnd}
AND SMELL_REGISTER_TIME = #{smellRegisterTime}
GROUP BY DATE_FORMAT(REG_DT, '%Y-%m-%d'), SMELL_REGISTER_TIME, REG_ID ) t2 WHERE 1=1 AND DATE_FORMAT(t1.REG_DT, '%Y-%m-%d') = DATE_FORMAT(t2.REG_DT, '%Y-%m-%d') AND t1.SMELL_REGISTER_TIME = t2.SMELL_REGISTER_TIME AND t1.REG_ID = t2.REG_ID AND t1.SMELL_VALUE = t2.SMELL_VALUE GROUP BY DATE_FORMAT(t1.REG_DT, '%Y-%m-%d'), t1.SMELL_REGISTER_TIME, t1.REG_ID ) userRegisterCount ) result LEFT OUTER JOIN ( SELECT smellValueResult.SMELL_VALUE mainSmellValue, smellTypeResult.SMELL_TYPE mainSmellType FROM ( SELECT SMELL_VALUE FROM ( SELECT t1.SMELL_VALUE SMELL_VALUE, t1.SMELL_REGISTER_TIME SMELL_REGISTER_TIME, t1.REG_ID REG_ID FROM tb_smell_register t1, ( SELECT MAX(SMELL_VALUE) SMELL_VALUE, REG_DT, SMELL_REGISTER_TIME, REG_ID FROM tb_smell_register WHERE 1 = 1 AND DATE_FORMAT(REG_DT, '%Y-%m-%d') BETWEEN #{searchStart} AND #{searchEnd} GROUP BY DATE_FORMAT(REG_DT, '%Y-%m-%d'), SMELL_REGISTER_TIME, REG_ID ) t2 WHERE 1 = 1 AND DATE_FORMAT(t1.REG_DT, '%Y-%m-%d') = DATE_FORMAT(t2.REG_DT, '%Y-%m-%d') AND t1.SMELL_REGISTER_TIME = t2.SMELL_REGISTER_TIME AND t1.REG_ID = t2.REG_ID AND t1.SMELL_VALUE = t2.SMELL_VALUE
AND t1.SMELL_REGISTER_TIME = #{smellRegisterTime}
AND t1.SMELL_VALUE != '001' GROUP BY DATE_FORMAT(t1.REG_DT, '%Y-%m-%d'), SMELL_REGISTER_TIME, REG_ID ) a Group by SMELL_VALUE ORDER BY COUNT(SMELL_VALUE) DESC, SMELL_VALUE DESC LIMIT 1 ) smellValueResult, ( SELECT SMELL_TYPE FROM ( SELECT t1.SMELL_TYPE SMELL_TYPE, t1.SMELL_REGISTER_TIME SMELL_REGISTER_TIME, t1.REG_ID REG_ID FROM tb_smell_register t1, ( SELECT MAX(SMELL_VALUE) SMELL_VALUE, REG_DT, SMELL_REGISTER_TIME, REG_ID FROM tb_smell_register WHERE 1 = 1 AND DATE_FORMAT(REG_DT, '%Y-%m-%d') BETWEEN #{searchStart} AND #{searchEnd} GROUP BY DATE_FORMAT(REG_DT, '%Y-%m-%d'), SMELL_REGISTER_TIME, REG_ID ) t2 WHERE 1 = 1 AND DATE_FORMAT(t1.REG_DT, '%Y-%m-%d') = DATE_FORMAT(t2.REG_DT, '%Y-%m-%d') AND t1.SMELL_REGISTER_TIME = t2.SMELL_REGISTER_TIME AND t1.REG_ID = t2.REG_ID
AND t1.SMELL_REGISTER_TIME = #{smellRegisterTime}
AND t1.SMELL_TYPE != '008' GROUP BY DATE_FORMAT(t1.REG_DT, '%Y-%m-%d'), SMELL_REGISTER_TIME, REG_ID ) a Group by SMELL_TYPE ORDER BY COUNT(SMELL_TYPE) DESC LIMIT 1 ) smellTypeResult ) smellResult ON 1=1

SELECT *, ROUND(ifNull(result.userRegisterCount/result.userTotalCount*100,0),2) userRegisterPercentage, ifNull(F_GET_CODE_NAME('SMT', result.mainSmellValue),'') mainSmellValueName, IFNULL(F_GET_CODE_NAME('STY',result.mainSmellType),'') mainSmellTypeName, #{smellRegisterTime} smellRegisterTime, ifNull(F_GET_CODE_NAME('REN',#{smellRegisterTime}),'') smellRegisterTimeName FROM( SELECT a.REFERENCE_CODE_ID userRegionMaster, a.CODE_ID userRegionDetail, a.USER_REGION_MASTER_NAME userRegionMasterName, a.USER_REGION_DETAIL_NAME userRegionDetailName, ifnull(b.USER_REGISTER_COUNT, 0) userRegisterCount, ifnull(b.USER_TOTAL_COUNT, 0) userTotalCount, ifNull(( SELECT SMELL_VALUE FROM ( SELECT a.USER_ID, a.USER_REGION_MASTER, a.USER_REGION_DETAIL FROM tb_user a INNER JOIN ( SELECT USER_ID, MAX(REG_GBN) REG_GBN FROM tb_user GROUP BY USER_ID) b ON a.USER_ID = b.USER_ID AND a.REG_GBN = b.REG_GBN ) tb_user, ( SELECT MAX(SMELL_VALUE) SMELL_VALUE , REG_ID, REG_DT FROM tb_smell_register WHERE 1=1 AND DATE_FORMAT(tb_smell_register.REG_DT, '%Y-%m-%d') BETWEEN #{searchStart} AND #{searchEnd}
AND SMELL_REGISTER_TIME = #{smellRegisterTime}
GROUP BY DATE_FORMAT(REG_DT, '%Y-%m-%d'), SMELL_REGISTER_TIME, REG_ID ) tb_smell_register WHERE 1 = 1 AND tb_smell_register.SMELL_VALUE != '001' AND tb_smell_register.REG_ID = tb_user.USER_ID AND tb_user.USER_REGION_MASTER = a.REFERENCE_CODE_ID AND tb_user.USER_REGION_DETAIL = a.CODE_ID GROUP BY tb_smell_register.SMELL_VALUE ORDER BY COUNT(SMELL_VALUE) DESC LIMIT 1 ),'') mainSmellValue, ifNull(( SELECT SMELL_TYPE FROM ( SELECT a.USER_ID, a.USER_REGION_MASTER, a.USER_REGION_DETAIL FROM tb_user a INNER JOIN ( SELECT USER_ID, MAX(REG_GBN) REG_GBN FROM tb_user GROUP BY USER_ID) b ON a.USER_ID = b.USER_ID AND a.REG_GBN = b.REG_GBN ) tb_user, ( SELECT t1.SMELL_VALUE, t1.SMELL_TYPE, t1.REG_ID FROM tb_smell_register t1, ( SELECT MAX(SMELL_VALUE) SMELL_VALUE, REG_ID, REG_DT, SMELL_REGISTER_TIME FROM tb_smell_register WHERE 1 = 1 AND DATE_FORMAT(tb_smell_register.REG_DT, '%Y-%m-%d') BETWEEN #{searchStart} AND #{searchEnd} GROUP BY DATE_FORMAT(REG_DT, '%Y-%m-%d'), SMELL_REGISTER_TIME, REG_ID ) t2 WHERE 1 = 1 AND DATE_FORMAT(t1.REG_DT, '%Y-%m-%d') = DATE_FORMAT(t2.REG_DT, '%Y-%m-%d') AND t1.SMELL_REGISTER_TIME = t2.SMELL_REGISTER_TIME AND t1.REG_ID = t2.REG_ID AND t1.SMELL_VALUE = t2.SMELL_VALUE
AND t1.SMELL_REGISTER_TIME = #{smellRegisterTime}
) tb_smell_register WHERE 1 = 1 AND tb_smell_register.SMELL_TYPE != '008' AND tb_smell_register.REG_ID = tb_user.USER_ID AND tb_user.USER_REGION_MASTER = a.REFERENCE_CODE_ID AND tb_user.USER_REGION_DETAIL = a.CODE_ID GROUP BY tb_smell_register.SMELL_TYPE ORDER BY COUNT(SMELL_TYPE) DESC LIMIT 1 ),'') mainSmellType FROM ( SELECT REFERENCE_CODE_ID, CODE_ID, ( SELECT CODE_ID_NAME FROM tb_code WHERE CODE_GROUP = 'REM' AND CODE_ID = tbCode.REFERENCE_CODE_ID ) USER_REGION_MASTER_NAME, CODE_ID_NAME USER_REGION_DETAIL_NAME FROM tb_code tbCode WHERE CODE_GROUP = 'RGD') a left outer join ( SELECT tb_user.USER_REGION_MASTER , tb_user.USER_REGION_DETAIL , COUNT(*) USER_TOTAL_COUNT , COUNT(CASE WHEN SMELL_VALUE != '001' AND SMELL_TYPE != '008' THEN 1 END) USER_REGISTER_COUNT FROM ( SELECT t1.SMELL_VALUE SMELL_VALUE, t1.SMELL_TYPE, t1.REG_DT REG_DT, t1.SMELL_REGISTER_TIME SMELL_REGISTER_TIME, t1.REG_ID REG_ID FROM tb_smell_register t1, ( SELECT MAX(SMELL_VALUE) SMELL_VALUE, REG_DT, SMELL_REGISTER_TIME, REG_ID FROM tb_smell_register WHERE 1 = 1 AND DATE_FORMAT(REG_DT, '%Y-%m-%d') BETWEEN #{searchStart} AND #{searchEnd}
AND SMELL_REGISTER_TIME = #{smellRegisterTime}
GROUP BY DATE_FORMAT(REG_DT, '%Y-%m-%d'), SMELL_REGISTER_TIME, REG_ID ) t2 WHERE 1=1 AND DATE_FORMAT(t1.REG_DT, '%Y-%m-%d') = DATE_FORMAT(t2.REG_DT, '%Y-%m-%d') AND t1.SMELL_REGISTER_TIME = t2.SMELL_REGISTER_TIME AND t1.REG_ID = t2.REG_ID AND t1.SMELL_VALUE = t2.SMELL_VALUE GROUP BY DATE_FORMAT(t1.REG_DT, '%Y-%m-%d'), t1.SMELL_REGISTER_TIME, t1.REG_ID )tb_smell_register, ( SELECT a.USER_ID USER_ID, a.USER_REGION_MASTER USER_REGION_MASTER, a.USER_REGION_DETAIL USER_REGION_DETAIL FROM tb_user a INNER JOIN ( SELECT USER_ID, MAX(REG_GBN) REG_GBN FROM tb_user GROUP BY USER_ID ) b ON a.USER_ID = b.USER_ID AND a.REG_GBN = b.REG_GBN ) tb_user WHERE 1 = 1 AND tb_smell_register.REG_ID = tb_user.USER_ID GROUP BY tb_user.USER_REGION_MASTER, tb_user.USER_REGION_DETAIL ) b ON a.REFERENCE_CODE_ID = b.USER_REGION_MASTER AND a.CODE_ID = b.USER_REGION_DETAIL ) result ORDER BY result.userRegionMaster, result.userRegionDetail

SELECT CASE WHEN CODE_ID IS NULL THEN '합계' ELSE (SELECT CODE_ID_NAME FROM tb_code WHERE tb_code.CODE_GROUP = 'REN' AND tb_code.CODE_ID = RESULT.CODE_ID) END REGISTER_TIME, IFNULL(USER_REG,'-') AS USER_REG, IFNULL(TOTAL_USER_REG_SMELL,'-') AS TOTAL_USER_REG_SMELL, IFNULL(SMELL_PERCENTAGE,'-') AS SMELL_PERCENTAGE, IFNULL(SMELL_LEVEL_1,'-') AS SMELL_LEVEL_1, IFNULL(SMELL_LEVEL_2,'-') AS SMELL_LEVEL_2, IFNULL(SMELL_LEVEL_3,'-') AS SMELL_LEVEL_3, IFNULL(SMELL_LEVEL_4,'-') AS SMELL_LEVEL_4, IFNULL(SMELL_LEVEL_5,'-') AS SMELL_LEVEL_5, USER_REGION_DETAIL_NAME FROM ( SELECT * FROM ( SELECT CODE_ID FROM TB_CODE WHERE CODE_GROUP = 'REN' ) TC RIGHT OUTER JOIN ( SELECT SMELL_REGISTER_TIME , USER_REG , USER_REG_SMELL AS TOTAL_USER_REG_SMELL , ROUND(IFNULL(USER_REG_SMELL / USER_REG * 100, 0), 2) AS SMELL_PERCENTAGE , SMELL_LEVEL_1 , SMELL_LEVEL_2 , SMELL_LEVEL_3 , SMELL_LEVEL_4 , SMELL_LEVEL_5 , USER_REGION_DETAIL_NAME FROM ( SELECT * FROM TB_CODE WHERE CODE_GROUP = 'STY' ) T1 RIGHT OUTER JOIN ( SELECT SMELL_TYPE, SMELL_REGISTER_TIME, COUNT(*) AS USER_REG, COUNT(CASE WHEN TSR.SMELL_VALUE != '001' AND TSR.SMELL_TYPE != '008' THEN 1 END) USER_REG_SMELL, COUNT(CASE WHEN TSR.SMELL_VALUE = '002' AND TSR.SMELL_TYPE != '008' THEN 1 END) SMELL_LEVEL_1, COUNT(CASE WHEN TSR.SMELL_VALUE = '003' AND TSR.SMELL_TYPE != '008' THEN 1 END) SMELL_LEVEL_2, COUNT(CASE WHEN TSR.SMELL_VALUE = '004' AND TSR.SMELL_TYPE != '008' THEN 1 END) SMELL_LEVEL_3, COUNT(CASE WHEN TSR.SMELL_VALUE = '005' AND TSR.SMELL_TYPE != '008' THEN 1 END) SMELL_LEVEL_4, COUNT(CASE WHEN TSR.SMELL_VALUE = '006' AND TSR.SMELL_TYPE != '008' THEN 1 END) SMELL_LEVEL_5, (SELECT CODE_ID_NAME FROM TB_CODE WHERE TB_CODE.CODE_GROUP = 'RGD' AND TB_CODE.CODE_ID = TU.USER_REGION_DETAIL) AS USER_REGION_DETAIL_NAME FROM TB_SMELL_REGISTER TSR LEFT OUTER JOIN TB_USER TU ON TSR.REG_ID = TU.USER_ID WHERE DATE_FORMAT(tsr.REG_DT, '%Y-%m-%d') BETWEEN #{searchStart} AND #{searchEnd}
AND tu.USER_REGION_DETAIL = #{userRegionDetail}
AND tsr.SMELL_TYPE = #{smellType}
GROUP BY SMELL_REGISTER_TIME WITH ROLLUP ) T2 ON T1.CODE_ID = T2.SMELL_TYPE ) T3 ON TC.CODE_ID = T3.SMELL_REGISTER_TIME UNION SELECT * FROM ( SELECT CODE_ID FROM TB_CODE WHERE CODE_GROUP = 'REN' ) TC LEFT OUTER JOIN ( SELECT SMELL_REGISTER_TIME , USER_REG , USER_REG_SMELL AS TOTAL_USER_REG_SMELL , ROUND(IFNULL(USER_REG_SMELL / USER_REG * 100, 0), 2) AS SMELL_PERCENTAGE , SMELL_LEVEL_1 , SMELL_LEVEL_2 , SMELL_LEVEL_3 , SMELL_LEVEL_4 , SMELL_LEVEL_5 , USER_REGION_DETAIL_NAME FROM ( SELECT * FROM TB_CODE WHERE CODE_GROUP = 'STY' ) T1 RIGHT OUTER JOIN ( SELECT SMELL_TYPE, SMELL_REGISTER_TIME, COUNT(*) AS USER_REG, COUNT(CASE WHEN TSR.SMELL_VALUE != '001' AND TSR.SMELL_TYPE != '008' THEN 1 END) USER_REG_SMELL, COUNT(CASE WHEN TSR.SMELL_VALUE = '002' AND TSR.SMELL_TYPE != '008' THEN 1 END) SMELL_LEVEL_1, COUNT(CASE WHEN TSR.SMELL_VALUE = '003' AND TSR.SMELL_TYPE != '008' THEN 1 END) SMELL_LEVEL_2, COUNT(CASE WHEN TSR.SMELL_VALUE = '004' AND TSR.SMELL_TYPE != '008' THEN 1 END) SMELL_LEVEL_3, COUNT(CASE WHEN TSR.SMELL_VALUE = '005' AND TSR.SMELL_TYPE != '008' THEN 1 END) SMELL_LEVEL_4, COUNT(CASE WHEN TSR.SMELL_VALUE = '006' AND TSR.SMELL_TYPE != '008' THEN 1 END) SMELL_LEVEL_5, (SELECT CODE_ID_NAME FROM TB_CODE WHERE TB_CODE.CODE_GROUP = 'RGD' AND TB_CODE.CODE_ID = TU.USER_REGION_DETAIL) AS USER_REGION_DETAIL_NAME FROM TB_SMELL_REGISTER TSR LEFT OUTER JOIN TB_USER TU ON TSR.REG_ID = TU.USER_ID WHERE DATE_FORMAT(tsr.REG_DT, '%Y-%m-%d') BETWEEN #{searchStart} AND #{searchEnd}
AND tu.USER_REGION_DETAIL = #{userRegionDetail}
AND tsr.SMELL_TYPE = #{smellType}
GROUP BY SMELL_REGISTER_TIME WITH ROLLUP ) T2 ON T1.CODE_ID = T2.SMELL_TYPE ) T3 ON TC.CODE_ID = T3.SMELL_REGISTER_TIME ) RESULT ORDER BY RESULT.CODE_ID ASC

SELECT * FROM ( SELECT CASE WHEN CODE_ID IS NULL THEN '합계' ELSE (SELECT TB_CODE.CODE_ID_NAME FROM TB_CODE WHERE TB_CODE.CODE_GROUP = 'STY' AND TB_CODE.CODE_ID = BB1.CODE_ID) END AS SMELL_TYPE , CASE WHEN USER_REG_SMELL = 0 OR USER_REG_SMELL IS NULL THEN '-' ELSE USER_REG_SMELL END AS TOTAL_USER_REG_SMELL , CASE WHEN SMELL_LEVEL_1 = 0 OR SMELL_LEVEL_1 IS NULL THEN '-' ELSE SMELL_LEVEL_1 END AS SMELL_LEVEL_1 , CASE WHEN SMELL_LEVEL_2 = 0 OR SMELL_LEVEL_2 IS NULL THEN '-' ELSE SMELL_LEVEL_2 END AS SMELL_LEVEL_2 , CASE WHEN SMELL_LEVEL_3 = 0 OR SMELL_LEVEL_3 IS NULL THEN '-' ELSE SMELL_LEVEL_3 END AS SMELL_LEVEL_3 , CASE WHEN SMELL_LEVEL_4 = 0 OR SMELL_LEVEL_4 IS NULL THEN '-' ELSE SMELL_LEVEL_4 END AS SMELL_LEVEL_4 , CASE WHEN SMELL_LEVEL_5 = 0 OR SMELL_LEVEL_5 IS NULL THEN '-' ELSE SMELL_LEVEL_5 END AS SMELL_LEVEL_5 FROM ( SELECT * FROM ( SELECT * FROM TB_CODE WHERE CODE_GROUP = 'STY' ) T1 LEFT OUTER JOIN ( SELECT SMELL_TYPE, COUNT(*) AS USER_REG, COUNT(CASE WHEN TSR.SMELL_VALUE != '001' AND TSR.SMELL_TYPE != '008' THEN 1 END) USER_REG_SMELL, COUNT(CASE WHEN TSR.SMELL_VALUE = '002' AND TSR.SMELL_TYPE != '008' THEN 1 END) SMELL_LEVEL_1, COUNT(CASE WHEN TSR.SMELL_VALUE = '003' AND TSR.SMELL_TYPE != '008' THEN 1 END) SMELL_LEVEL_2, COUNT(CASE WHEN TSR.SMELL_VALUE = '004' AND TSR.SMELL_TYPE != '008' THEN 1 END) SMELL_LEVEL_3, COUNT(CASE WHEN TSR.SMELL_VALUE = '005' AND TSR.SMELL_TYPE != '008' THEN 1 END) SMELL_LEVEL_4, COUNT(CASE WHEN TSR.SMELL_VALUE = '006' AND TSR.SMELL_TYPE != '008' THEN 1 END) SMELL_LEVEL_5, TU.USER_REGION_DETAIL FROM TB_SMELL_REGISTER TSR LEFT OUTER JOIN TB_USER TU ON TSR.REG_ID = TU.USER_ID WHERE DATE_FORMAT(tsr.REG_DT, '%Y-%m-%d') BETWEEN #{searchStart} AND #{searchEnd}
AND tu.USER_REGION_DETAIL = #{userRegionDetail}
AND tsr.SMELL_TYPE = #{smellType}
GROUP BY TSR.SMELL_TYPE WITH ROLLUP ) T2 ON T1.CODE_ID = T2.SMELL_TYPE UNION SELECT * FROM ( SELECT * FROM TB_CODE WHERE CODE_GROUP = 'STY' ) T1 RIGHT OUTER JOIN ( SELECT SMELL_TYPE, COUNT(*) AS USER_REG, COUNT(CASE WHEN TSR.SMELL_VALUE != '001' AND TSR.SMELL_TYPE != '008' THEN 1 END) USER_REG_SMELL, COUNT(CASE WHEN TSR.SMELL_VALUE = '002' AND TSR.SMELL_TYPE != '008' THEN 1 END) SMELL_LEVEL_1, COUNT(CASE WHEN TSR.SMELL_VALUE = '003' AND TSR.SMELL_TYPE != '008' THEN 1 END) SMELL_LEVEL_2, COUNT(CASE WHEN TSR.SMELL_VALUE = '004' AND TSR.SMELL_TYPE != '008' THEN 1 END) SMELL_LEVEL_3, COUNT(CASE WHEN TSR.SMELL_VALUE = '005' AND TSR.SMELL_TYPE != '008' THEN 1 END) SMELL_LEVEL_4, COUNT(CASE WHEN TSR.SMELL_VALUE = '006' AND TSR.SMELL_TYPE != '008' THEN 1 END) SMELL_LEVEL_5, TU.USER_REGION_DETAIL FROM TB_SMELL_REGISTER TSR LEFT OUTER JOIN TB_USER TU ON TSR.REG_ID = TU.USER_ID WHERE DATE_FORMAT(tsr.REG_DT, '%Y-%m-%d') BETWEEN #{searchStart} AND #{searchEnd}
AND tu.USER_REGION_DETAIL = #{userRegionDetail}
AND tsr.SMELL_TYPE = #{smellType}
GROUP BY TSR.SMELL_TYPE WITH ROLLUP ) T2 ON T1.CODE_ID = T2.SMELL_TYPE ) BB1 ) RESULT ORDER BY RESULT.SMELL_TYPE DESC
